import javax.swing.*;

public class Main {
    public static void main(String[] args) {
        // Ingresar cantidad de jugadores
        int numJugadores = 1;
        try {
            numJugadores = Integer.parseInt(JOptionPane.showInputDialog("¿Cuántos jugadores participarán?"));
        } catch (Exception e) { }
        if (numJugadores < 1) numJugadores = 1;

        String[] roles = {"Guerrero", "Explorador"};
        Jugador[] jugadores = new Jugador[numJugadores];
        for (int i = 0; i < numJugadores; i++) {
            String nombre = JOptionPane.showInputDialog("Nombre del jugador " + (i+1) + ":");
            int rol = JOptionPane.showOptionDialog(null, "Elige rol para " + nombre, "Jugador",
                    0, JOptionPane.PLAIN_MESSAGE, null, roles, roles[0]);
            if (rol == 0)
                jugadores[i] = new Guerrero(nombre);
            else
                jugadores[i] = new Explorador(nombre);
        }

        // Ingresar cantidad de enemigos
        int numEnemigos = 1;
        try {
            numEnemigos = Integer.parseInt(JOptionPane.showInputDialog("¿Cuántos enemigos habrá?"));
        } catch (Exception e) { }
        if (numEnemigos < 1) numEnemigos = 1;

        Combatiente[] enemigos = new Combatiente[numEnemigos];
        String[] tipos = {"Básico", "Jefe"};
        for (int i = 0; i < numEnemigos; i++) {
            int tipo = JOptionPane.showOptionDialog(null, "Tipo de enemigo " + (i+1), "Enemigo",
                    0, JOptionPane.PLAIN_MESSAGE, null, tipos, tipos[0]);
            String nombre = JOptionPane.showInputDialog("Nombre del enemigo " + (i+1) + ":");
            if (tipo == 1)
                enemigos[i] = new Jefe(nombre);
            else
                enemigos[i] = new Basico(nombre);
        }

        Controller.iniciarJuego(jugadores, enemigos);
    }
}

public class Controller {
    public static void iniciarJuego(Jugador[] jugadores, Combatiente[] enemigos) {
        Batalla batalla = new Batalla(jugadores, enemigos);
        batalla.iniciar();
    }
}

public class Batalla {
    private Jugador[] jugadores;
    private Combatiente[] enemigos;

    public Batalla(Jugador[] jugadores, Combatiente[] enemigos) {
        this.jugadores = jugadores;
        this.enemigos = enemigos;
    }

    public void iniciar() {
        for (Jugador j : jugadores) {
            System.out.println(j.mensajeInicio());
        }
        for (Combatiente e : enemigos) {
            System.out.println(e.mensajeInicio());
        }

        while (hayJugadoresVivos() && hayEnemigosVivos()) {
            Vista.mostrarEstado(jugadores, enemigos);
            for (Jugador j : jugadores) {
                if (j.estaVivo()) {
                    Vista.menuTurnoJugador(j, enemigos);
                }
            }
            for (Combatiente e : enemigos) {
                if (e.estaVivo()) {
                    e.turno(enemigos, jugadores);
                }
            }
        }

        if (hayJugadoresVivos()) {
            System.out.println("¡Ganaron los jugadores la batalla!");
        } else {
            for (Jugador j : jugadores) {
                if (!j.estaVivo()) System.out.println(j.mensajeMuerte());
            }
        }
    }

    private boolean hayEnemigosVivos() {
        for (Combatiente e : enemigos) if (e.estaVivo()) return true;
        return false;
    }

    private boolean hayJugadoresVivos() {
        for (Jugador j : jugadores) if (j.estaVivo()) return true;
        return false;
    }
}

import javax.swing.*;

public class Vista {
    public static void mostrarEstado(Jugador[] jugadores, Combatiente[] enemigos) {
        StringBuilder sb = new StringBuilder();
        for (Jugador jugador : jugadores) {
            sb.append("Jugador: ").append(jugador.getNombre()).append(" (Vida: ").append(jugador.getVida()).append(")\n");
        }
        for (Combatiente e : enemigos) {
            sb.append(e.getNombre()).append(" (Vida: ").append(e.getVida()).append(")\n");
        }
        JOptionPane.showMessageDialog(null, sb.toString(), "Estado", JOptionPane.INFORMATION_MESSAGE);
    }

    public static void menuTurnoJugador(Jugador jugador, Combatiente[] enemigos) {
        String[] opciones = {"Atacar", "Usar Ítem", "Pasar"};
        int eleccion = JOptionPane.showOptionDialog(null, "Turno de " + jugador.getNombre(),
                "Acción", 0, JOptionPane.PLAIN_MESSAGE, null, opciones, opciones[0]);

        switch (eleccion) {
            case 0: // Atacar
                Combatiente objetivo = seleccionarObjetivo(enemigos);
                if (objetivo != null) jugador.atacar(objetivo);
                break;
            case 1: // Usar ítem
                if (!jugador.getInventario().isEmpty()) {
                    Item item = (Item) JOptionPane.showInputDialog(null, "Selecciona ítem",
                            "Ítems", JOptionPane.PLAIN_MESSAGE, null,
                            jugador.getInventario().toArray(), jugador.getInventario().get(0));
                    Combatiente obj = seleccionarObjetivo(enemigos);
                    if (obj != null) jugador.usarItem(item, obj);
                } else {
                    JOptionPane.showMessageDialog(null, "No tienes ítems!");
                }
                break;
            default: // Pasar
                JOptionPane.showMessageDialog(null, jugador.getNombre() + " pasa el turno.");
        }
    }

    public static Combatiente seleccionarObjetivo(Combatiente[] enemigos) {
        String[] nombres = java.util.Arrays.stream(enemigos).filter(Combatiente::estaVivo)
                .map(Combatiente::getNombre).toArray(String[]::new);
        if (nombres.length == 0) return null;
        String elegido = (String) JOptionPane.showInputDialog(null, "Elige objetivo",
                "Objetivo", JOptionPane.PLAIN_MESSAGE, null, nombres, nombres[0]);
        for (Combatiente e : enemigos) if (e.getNombre().equals(elegido)) return e;
        return null;
    }
}

